namespace Builderius\Symfony\Component\Cache\Traits;

use Builderius\Symfony\Component\Cache\PruneableInterface;
use Builderius\Symfony\Contracts\Service\ResetInterface;
/**
 * @author Nicolas Grekas <p@tchwork.com>
 *
 * @internal
 */
trait ProxyTrait
{
    private $pool;
    /**
     * {@inheritdoc}
     */
    public function prune()
    {
        return $this->pool instanceof \Builderius\Symfony\Component\Cache\PruneableInterface && $this->pool->prune();
    }
    /**
     * {@inheritdoc}
     */
    public function reset()
    {
        if ($this->pool instanceof \Builderius\Symfony\Contracts\Service\ResetInterface) {
            $this->pool->reset();
        }
    }
}
